mapper.select:
select
from hg_article t where t.user_id = #{userId} and t.hold = 0 and t.type = #{type}
and t.platform = #{platform}
order by t.create_date desc, t.platform asc

select
from hg_article t where t.user_id = #{userId} and t.hold = 0 and t.type = #{type} and t.platform in
#{platform}
and t.wechat = #{wechat}
and t.active = 1 order by t.create_date desc, t.platform asc

mapper.update:
update hg_article t set t.hold = #{hold}, t.visit_count = 0
where
  t.user_id = #{userId} and t.platform = #{platform} and t.type = #{type} and t.wechat = #{wechat}

update hg_article t set t.url = #{url}, t.visit_count = 0
where
  t.user_id = #{userId} and t.platform = #{platform} and t.type = #{type} and t.wechat = #{wechat}

mapper.select:
select count(t.id) from hg_article t
where
  t.user_id = #{userId} and t.platform = #{platform} and t.type = #{type} and t.wechat = #{wechat}
